import React,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
// import 'bootstrap/dist/css/bootstrap-theme.min.css';
// import $ from 'jquery';
// import Popper from 'popper.js';
// import 'bootstrap/dist/js/bootstrap.bundle.min';
// import 'bootstrap-4-react';
import {connect } from 'react-redux';
import{ selectBook} from'../actions/index'
import    {bindActionCreators}  from 'redux'


class BookList extends  Component {
      renderList(){
      return    this.props.books.map((books) =>{
          return(
              <div>
                  <li key={books.title} className="list-group-item" >{books.title}</li>
              </div>
          )
      })
    }
    render(){



        return(
            <div>
              <ul className='list-group col-sm-4'>
                {this.renderList()}
               </ul>
            </div>
        )
    }
}
function mapSateToProps(state) {
    //whatever is retured will show up as props
    //inside of Booklist
    return{
    books: state.books
    }
}

function mapDispatchToProps(dispatch) {

    //whenever selestbook is called the result should be passed
    //to all of our redu
    return bindActionCreators({selectBook: selectBook}, {dispatch})

}


export default connect(mapSateToProps, mapDispatchToProps)(BookList)
