import { GET_CATEGORIES, ADD_CATEGORIE, REMOVE_CATEGORIE } from '../actions/Types';

const initailState = {
	categories: [],
	isLoading: false
};

export default (state = initailState, action) => {
	switch (action.type) {
		case GET_CATEGORIES:
			return {
				...state,
				categories: action.payload,
				isLoading:true
			};
			break;

		default:
            return { ...state };
	}
};
