import {combineReducers} from 'redux';

const songsReducer = () =>{
    return[
          {title: "No Srubs", duratration: "4:50s"},
          {title: "No fortune", duratration: "3:50s"},
          {title: "No yes", duratration: "2:50s"},
          {title: "No ok", duratration: "1:50s"}
    ]


}


const selectedSongReducer = (selectedSong = null, action) => {
    if (action.type === 'SONG_SELECTED') {
        return action.payload;
    }

    return selectedSong;
}


combineReducers
