import { GET_CATEGORIES, ADD_CATEGORIE, REMOVE_CATEGORIE } from './Types';
import axios from 'axios';
const url = `http://localhost:3000/api/routes/categories`
export const getCATEGORIES = async () => (dispatch) => {

		axios.get(url)
		.then
        	// dispatch ({
    //     type: GET_CATEGORIES,
    //     payload: res.data
	// });


};

export const removeCATEGORIE = (_id) => (dispatch) => {
	return {
		type: REMOVE_CATEGORIE,
		payload: _id
	};
};

export const addCATEGORIE = (item) => (dispatch) => {
	return {
		type: ADD_CATEGORIE,
		payload: item
	};
};
